import React, { Component } from 'react'

export class Categories extends Component {

    constructor(props) {
        super(props) 
        this.state = {
            categories: [
                {
                    key:'all',
                    name:'Всі товари'
                },
                {
                    key:'chairs',
                    name:'Стільці'
                },
                {
                    key:'tables',
                    name:'Столи'
                },
                {
                    key:'sofa',
                    name:'Дивани'
                },
                {
                    key:'light',
                    name:'Світло'
                },
            ]
        }
    }

render() {
    return (
    <div className='categories'>
        {this.state.categories.map(el=>(
            <div key={el.key}>{el.name}</div>
        ))}
        </div>
    )
}
}

export default Categories
